class Solution {
public:
    long long countSubarrays(vector<int>& arr, int tgt) {
        int n = arr.size();
        long long count = 0;
        unordered_map<long long, long long> prev;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> curr; 
            if (arr[i] == tgt) {
                ++count;
            }
            curr[arr[i]] = 1;
            for (auto& [val, freq] : prev) {
                long long andRes = val & arr[i];
                if (andRes == tgt) {
                    count += freq;
                }
                curr[andRes] += freq;
            }
            prev = curr;
        }
        return count;
    }
};
