class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;
        map<long long,long long> mp;
        for(int i=0;i<n;i++){
            map<long long,long long> temp;
            temp[nums[i]]=1;
            for(auto& [v,j]:mp){
                long long val=v&nums[i];
                if(val==k) ans+=j;
                temp[val]+=j;
            }
            if(nums[i]==k)ans++;
            mp=temp;
        }
        return ans;
    }
};
