unordered_map<long long,long long>mp,mp1;
class Solution {
public:
    void updateCurrentValue(int value){
        mp1[value]=1;
    }
    long long int getAnd(long long int a,int b){
        return (a&b);
    }
    void updateFreq(int a,int b){
        mp1[a]+=b;
    }
    void updateFinalAns(long long int &a,int b){
        a+=b;
    }
    void updateMap(){
        mp=mp1;
    }
    void solve(long long int &ans,int num,int k){
        long long int temp=0;
        for(auto it:mp){
            temp=getAnd(it.first,num);if(temp==k) updateFinalAns(ans,it.second);
            updateFreq(temp,it.second);
        }
        updateMap();
    }
    long long countSubarrays(vector<int>& nums, int k) {
        ios_base::sync_with_stdio(false);cin.tie(0);cout.tie(0);
        int n=nums.size(); long long int ans=0;
        mp=unordered_map<long long,long long>();
        for(int num:nums){if(num==k) ans++;
            mp1=unordered_map<long long,long long>();updateCurrentValue(num);solve(ans,num,k);
        }
        return ans;
    }
};
