import java.util.HashMap;

class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long count = 0;

        HashMap<Long,Long> prev = new HashMap<>();

        for (int i = 0; i < n; ++i) {
            HashMap<Long, Long> curr = new HashMap<>();

            if (nums[i] == k) {
                ++count;
            }
            curr.put((long) nums[i], 1L);

            for (HashMap.Entry<Long, Long> entry : prev.entrySet()) {
                long newres = entry.getKey() & nums[i];
                if (newres == k) {
                    count += entry.getValue();
                }
                curr.put(newres, curr.getOrDefault(newres, 0L) + entry.getValue());
            }

            prev = curr;
        }

        return count;
    }
}
