class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
          long long ans=0;
      unordered_map<long long,long long>mp;
        for(auto &it:nums){
            unordered_map<long long, long long>mp1;
             for(auto &itr:mp){
                long long val=itr.first&it;
                 mp1[val]+=itr.second;
             }
             mp1[it]++;
             for(auto &itrr:mp1){
                if(itrr.first==k) ans+=itrr.second;
             }
             mp=mp1;
        }
        return ans;
    }
};
