class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        map<int, int> mp;
        long long ans = 0;
        for (int i=1; i<=n; i++){
            if ((a[i-1] & k) == k){
                int mmax = 0, mmin = i;
                for (int j=0; j<=30; j++){
                    if (k>>j & 1) mmax = max(mmax, mp[j]);
                }
                for (int j=0; j<=30; j++){
                    if (a[i-1] >> j & 1){
                        if (k>>j & 1) continue;
                        mmin = min(mmin, mp[j]);
                    }
                }
                ans+=max(mmin-mmax, 0);
            } 
            for (int j=0; j<=30; j++){
                if (!(a[i-1]>>j & 1)){
                    mp[j] = i;
                }
            }

        }
        return ans;
    }
};
