class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
         long long ans=0;
        unordered_map<long long, long long>map;
        for (int i=0;i<n;i++){
            unordered_map<long long, long long>mp1; 
            if(nums[i]==k)ans++;                
            mp1[nums[i]]=1;
            for(auto&[it,x]:map) {
                long long p1=it&nums[i];
                if(p1==k)ans+=x;
                 mp1[p1]+=x;
            }           
        map=mp1;
        }
        return ans;
    }
};
