#include <vector>
#include <unordered_map>

class Solution {
public:
    long long countSubarrays(std::vector<int>& array, int target) {
        int size = array.size();
        long long total = 0;
        std::unordered_map<long long, long long> previousResults;

        for (int index = 0; index < size; ++index) {
            std::unordered_map<long long, long long> currentResults;

            if (array[index] == target) {
                ++total;
            }

            currentResults[array[index]] = 1;

            for (auto& pair : previousResults) {
                long long value = pair.first;
                long long frequency = pair.second;

                long long newAndResult = value & array[index];

                if (newAndResult == target) {
                    total += frequency;
                }

                currentResults[newAndResult] += frequency;
            }

            previousResults = currentResults;
        }

        return total;
    }
};
