class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

        unordered_map <int, int> mp;
        unordered_map <int, long long> ans;



        for(int i = 0; i<nums.size(); i++){
            unordered_map <int, int> temp;

            //mp[0]++;
            mp[nums[i]]++;
            for(auto v : mp){
                int val = v.first&nums[i];
                temp[val] += v.second;
            }

            for(auto v : temp){
                ans[v.first] += v.second;
            }

            mp = temp;
        }


        return ans[k];


    }
};
