#define FOR(i, a, b) for (int i = a; i <= b; i++)
#define REP(i, a) for (int i = 0; i < a; i++)
#define ALL(v) (v).begin(), (v).end()
#define SORT(v) sort(ALL(v))
#define F first
#define S second

const int INF = 1e9;
const ll LINF = 1e18;
const int MOD = 1e9 + 7;
class  Solution {
public:
    ll countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll ct = 0;

        unordered_map<ll, ll> pr;

        REP(i,n){
            unordered_map<ll, ll> cr; 

            if (nums[i] == k) {
                ++ct;
            }
            cr[nums[i]] = 1;

            for (auto& [val, freq] : pr) {
                ll nr = val & nums[i];
                if (nr == k) {
                    ct += freq;
                }
                cr[nr] += freq;
            }

            pr = cr;
        }

        return ct;
    }
};
