class Solution {
public:
    typedef long long ll;
    ll solve(vector<int>&nums,int k,unordered_map<ll,ll>&mp){
        ll j=0,ans=0;
        while(j<nums.size()){
            unordered_map<ll,ll>x;
            if(nums[j]==k)ans++;
            x[nums[j]]=1;
            for(auto& val:mp){
                if((val.first&nums[j])==k)ans+=val.second;
                x[val.first&nums[j]]+=val.second;      
            }
            mp=x;j++;
        }
        return ans;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<ll,ll>mp;
        return solve(nums,k,mp);
    }
};
