class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;
        int arr[10];
        unordered_map<long long, long long> boor1;
        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> boor2; 
            if (nums[i] == k) count++;
            boor2[nums[i]] = 1;
            for (auto& [vali, freq] : boor1) {
                long long newAndResult = vali & nums[i];
                if (newAndResult == k) {
                    count += freq;
                }
               boor2[newAndResult] += freq;
            }
            boor1=boor2;
        }
       for(int i=0;i<10;i++){
        arr[i]=i;
       }
        return count;
    }
};
