class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0
        old_result = {}

        for i in range(n):
            new_result = {}
            if nums[i] == k:
                count += 1

            new_result[nums[i]] = 1

            for value, frequency in old_result.items():
                and_result = (value&nums[i])

                if and_result == k:
                    count += frequency

                new_result[and_result] = new_result.get(and_result, 0) + frequency

            old_result = new_result

        return count
