#define ll long long
class Solution {
public:
    void helper(map<ll, ll>& mp,ll &res,map<ll, ll>& mp2,vector<int>& nums,int i,int k){
        for (auto p : mp) {
                ll cnt = p.first & nums[i];
                if (cnt == k) {
                    res += p.second;
                }
                mp2[cnt] += p.second;
            }
    }

    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        ll res = 0;
        std::map<ll, ll> mp;
        for (int i = 0; i < n; ++i) {
            std::map<ll, ll> mp2; 
            if (nums[i] == k) {
                res++;
            }
            mp2[nums[i]] = 1;
            helper(mp,res,mp2,nums,i,k);
            mp = mp2;
        }
        return res;
    }
};
