class Solution {
    public long countSubarrays(int[] nums, int k) {

        long ans=0;
        HashMap<Integer,Long> hm=new HashMap<>();
        int n=nums.length;
        for(int i=0;i<n;i++){
            if(nums[i]==k) ans++;

            HashMap<Integer,Long> curr_map=new HashMap<>();
            curr_map.put(nums[i], 1L);

            for(int key: hm.keySet()){
                int and=key&nums[i];
                curr_map.put(and, curr_map.getOrDefault(and, 0L)+hm.get(key));
                if(and==k) ans+=hm.get(key);
            }
            hm=curr_map;
        }
        return ans;
    }
}
