class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long long, long long>mp;
        int n=nums.size();
        long long result=0;
        for(int i=0;i<n;i++){
            unordered_map<long long, long long>newMap;
            for(auto it : mp){
                long long x= it.first & nums[i];
                newMap[x]+=it.second;
            }
            newMap[nums[i]]++;
             mp=newMap;
            if(mp.find(k)!=mp.end()){
                result+=mp[k];
            }
        }
        return result;
    }
};
