class Solution {
public:
    long long countSubarrays(vector<int>& ar, int k) {
        long long ans = 0;

        unordered_map<long long, long long> bef;

        for (int i = 0; i < ar.size(); ++i) 
        {
            unordered_map<long long, long long> pre;
            if (ar[i] == k) 
                ans++;
            pre[ar[i]] = 1;
            for (auto& [val, freq] : bef) {
                long long x = val & ar[i];
                if (x == k) 
                    ans += freq;
                pre[x] += freq;
            }
            bef = pre;
        }
        return ans;
    }
};
