class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        total = 0
        prev = {}

        for i in range(len(nums)):
            curr = {}
            if nums[i] == k:
                total += 1
            curr[nums[i]] = 1

            for key, value in prev.items():
                new_res = key & nums[i]
                if new_res == k:
                    total += value
                if new_res in curr:
                    curr[new_res] += value
                else:
                    curr[new_res] = value

            prev = curr

        return total
