import java.util.HashMap;
import java.util.Map;

public class Solution {
    public long countSubarrays(int[] array, int target) {
        int size = array.length;
        long total = 0;
        Map<Long, Long> prevSubarrays = new HashMap<>();

        for (int index = 0; index < size; ++index) {
            Map<Long, Long> currSubarrays = new HashMap<>();

            if (array[index] == target) {
                ++total;
            }

            currSubarrays.put((long) array[index], 1L);

            for (Map.Entry<Long, Long> entry : prevSubarrays.entrySet()) {
                long value = entry.getKey();
                long frequency = entry.getValue();
                long newAndResult = value & array[index];

                if (newAndResult == target) {
                    total += frequency;
                }

                currSubarrays.put(newAndResult, currSubarrays.getOrDefault(newAndResult, 0L) + frequency);
            }

            prevSubarrays = currSubarrays;
        }

        return total;
    }
}
