class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int,int>prefix;
        long long ans = 0;

        for(int num:nums){
            unordered_map<int,int>curr;
            if(num==k) ans++;
            curr[num]=1;
            for(auto it:prefix){
                int val = it.first;
                int no = it.second;
                if((val & num) == k){
                    ans+=no;
                }
                curr[(val&num)]+=no;
            }
            prefix.clear();
            for(auto it:curr){
                prefix[it.first]=it.second;
            }
        }

        return ans;
    }
};
