class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;
        map<long long,long long> mp;
        for(int i=0;i<n;i++){
            map<long long,long long> mp1;
            if(nums[i]==k){
                ans++;
            }
            mp1[nums[i]]=1;
            for(auto it:mp){
                long long x=(it.first & nums[i]);
                if(x==k){
                    ans+=it.second;
                }
                mp1[x]+=it.second;
            }
            mp=mp1;
        }
        return ans;
    }
};
