class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int jo=nums.size();
        long long co_unt=0;
        unordered_map<long long,long long>preev_ress;
        for(int i=0;i<jo;i++) {
            unordered_map<long long,long long>cur_ress;
            if(nums[i]==k) {
                ++co_unt;

            }
            cur_ress[nums[i]]=1;
            for(auto&[val_l,fr_eeq]:preev_ress) {
                long long sol=val_l&nums[i];
                if(sol==k) {
                    co_unt+=fr_eeq;
                }
                cur_ress[sol]+=fr_eeq;
             }
            preev_ress=cur_ress;
        }
        return co_unt;

    }
};
