class Solution {
public:
     long long countSubarrays(vector<int>& nums, int k) {
        int lambai = nums.size();
        long long bansal = 0;
        map<long long, long long> shubham;
        for (int i = 0; i < lambai; ++i) {
            map<long long, long long> kannu; 
            if (nums[i] == k) {
                ++bansal;
            }
            kannu[nums[i]] = 1;
            for (auto& [value, frequency] : shubham) {
                long long jha = value & nums[i];
                if (jha == k) {
                    bansal += frequency;
                }
                kannu[jha] += frequency;
            }

            shubham = kannu;
        }
        return bansal;
    }
};
