class Solution {
public:
    long long countSubarrays(vector<int>&nums, int k) {
        int n = nums.size();
        long long ans= 0;
        unordered_map<long long, long long>mp;
        for (int i = 0; i <n; ++i) {
            unordered_map<long long, long long>mp2; 
            if (nums[i] == k) {
                ++ans;
            }
            mp2[nums[i]] = 1;
            for (auto& [value, frequency] : mp) {
                long long temp = value & nums[i];
                if (temp == k) {
                   ans+= frequency;
                }
                mp2[temp] += frequency;
            }

            mp = mp2;
        }
        return ans;
    }
};
