class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        total = 0
        previous = {}
        for index in range(len(nums)):
            current = {}
            if nums[index] == k:
                total += 1
            current[nums[index]] = 1
            for value, frequency in previous.items():
                result = value & nums[index]
                if result == k:
                    total += frequency
                if result in current:
                    current[result] += frequency
                else:
                    current[result] = frequency

            previous = current

        return total
