class Solution {
public:
    long long countSubarrays(vector<int>& array, int target)

    {

          int s = array.size();
        long long tot = 0;
      // int size = array.size();

        unordered_map<long long, long long> res;
        for (int index = 0; index < s; ++index)
        {
            unordered_map<long long, long long> ans; 
            if (array[index] == target) 

                tot++;

                ans[array[index]] = 1;
            for (auto& [value, frequency] : res)
            {

                long long newAndResult = value & array[index];
                if (newAndResult == target)   tot += frequency;


                ans[newAndResult] += frequency;
            }

                    res = ans;

                } 

        return tot;

    }
};
