class Solution {
public:
    typedef long long ll;
    long long countSubarrays(vector<int>& nums, int k) {
        ll n = nums.size(), res = 0;
        unordered_map <ll, ll> mpp1;
        unordered_map <ll, ll> mpp2;
        for(auto& ele: nums) {
            if(ele == k) {
                res++;
            }
            for(auto& it: mpp1) {
                ll t = it.first & ele;
                if(t == k) {
                    res += it.second;
                }
                mpp2[t] += it.second;
            }
            mpp2[ele]++;
            mpp1 = mpp2;
            mpp2 = unordered_map<ll, ll>();
        }
        return res;
    }
};
