class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long long, long long> mp;
        long long res = 0;
        int n = nums.size();

        for (int  i = 0; i <= n-1; i++) {
            unordered_map<long long, long long>  mp1;
            for (auto &entry : mp) {
                long long newAnd = entry.first & nums[i];
                mp1[newAnd] += entry.second;
            }
            mp1[nums[i]]++;
            mp = mp1;

            if (mp.find(k) != mp.end()) res += mp[k];

        }

        return res;
    }
};
