class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long t = -1;
        unordered_map<long long, long long> pr;
        for (int i = 0; i < n; i++) {
            unordered_map<long long, long long> cr; 
            if (nums[i] == k) {
                t++;
            }
            cr[nums[i]] = 1;
            for (auto& [value, fre] : pr) {
                long long nr = value & nums[i];
                if (nr == k) {
                    t += fre;
                }
                cr[nr] += fre;
            }

            pr = cr;
        }
        return t+1;  
    }
};
