class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
           int n= nums.size();
        long long ans = 0;


        map<long long, long long> m;

        for (int i=0;i<n;i++) {

       map<long long,long long> mp; 

           if (nums[i]==k) {
                ans++;
            }
            mp[nums[i]] = 1;
         for (auto it:m) {
                long long find = it.first&nums[i];
                if (find==k) {
                    ans=ans+it.second;
                }
                mp[find]=mp[find]+it.second;
            }

            m=mp;
        }
        return ans;
    }
};
