class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {        
        int n=nums.size();
        unordered_map<long long,long long> prev;
        long long res=0;
        for(int i=0;i<n;i++) {
            unordered_map<long long,long long> curr;
            if(nums[i] == k) res++;
            curr[nums[i]]=1;
            for(auto it:prev) {
                long long total = it.first & nums[i];
                if(total == k) res += it.second;
                curr[total] += it.second;
            }
            prev=curr;
        }
        return res;
    }
};
