class Solution {
public:
    int n;
    unordered_map<ll, ll> mp1;
    long long countSubarrays(vector<int>& v, int k) {
        n = v.size();
        ll c = 0;

        for (size_t i = 0; i < n; i++) {

            unordered_map<ll, ll> mp2; 
            if (v[i] == k) c++;

            mp2[v[i]] = 1;

            for (auto it : mp1) {

                ll x = it.first;
                ll y = it.second;

                ll temp = x & v[i];

                if (temp == k) {
                    c += y;
                }

                mp2[temp] += y;
            }

            mp1 = mp2;
        }
        return c;
    }
};
