class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        vector<pair<int, int>> vp;
        long long ans = 0;
        for (int l = 0; l < n; l ++) {
            for (auto &[a, b]: vp)
                a &= nums[l];
            vp.emplace_back(nums[l], 1);
            map<int, int> mp;
            for (auto [a, b]: vp)
                mp[a] += b;
            vp.clear();
            for (auto [a, b]: mp)
                vp.push_back({a, b});
            ans += mp[k];
        }
        return ans;
    }
};
