class Solution {
public:
    long long countSubarrays(vector<int>& array, int k) {
        int size = array.size();
        long long tota = 0;
        unordered_map<long long, long long> previous;
        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> curr; 
            if (array[index] == k) {
                ++tota;
            }
            curr[array[index]] = 1;
            for (auto& [value, quency] : previous) {
                long long newAndResult = value & array[index];
                if (newAndResult == k) {
                    tota += quency;
                }
               curr[newAndResult] += quency;
            }

            previous = curr;
        }
        return tota;
    }
};
