class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {

       long long ans = 0;
        int n = nums.size();
        unordered_map<long long, long long> prev;
        int i = 0;
        while (i < n) {
            unordered_map<long long, long long> cur; 
            cur[nums[i]] = 1;
            for (auto it : prev) {
                 int val=it.first;
                 int freq=it.second;
                long long newAnd = (val & nums[i]);
                if (newAnd == k) {
                    ans += freq;
                }
                cur[newAnd] += freq;
            }
            if (nums[i] == k) {
                ans++;
            }
            prev = cur;
            i++;
        }
        return ans;

    }
};
