class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        unordered_map<long long,long long>mp1;
        long long ans=0;
        for(int i=0;i<n;i++){
             if(nums[i]==k) ans++;
            unordered_map<long long,long long>mp2;
            mp2[nums[i]]=1;

            for(auto it:mp1){
                long long tmp=it.first&nums[i];
                if(tmp==k){
                    ans+=it.second;
                }
                mp2[tmp]+=it.second;
            }
            mp1=mp2;
        }
        return ans;
    }
};
