class Solution {
public:
    long long countSubarrays(vector<int>& ar, int k) {
        map<int, long long> m;
        long long cnt = 0;
        for(const int i : ar) {
            map<int, long long> n;
            n[i]++;
            for(const auto [a, b] : m) {
                int t = a & i;
                n[t] += b;
            }
            cnt += n[k];
            swap(n, m);
        }
        return cnt;
    }
};
