class Solution {
public:
    using ll = long long ;
    long long countSubarrays(vector<int>& nums, int k) {
        map<int,ll> mp;
        int n = nums.size();ll ans = 0;
        for(int num:nums){
            map<int,ll> tmp;
            for(auto[k,v]:mp){
                tmp[k&num]+=v;
            }tmp[num]++;
            cout<<num<<endl;
            //for(auto[k,v]:tmp) cout<<k<<' '<<v<<endl;
            ans+=tmp[k];
            mp=move(tmp);
        }return ans;
    }
};
