class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long n = nums.size(),c=0;


        map<long long,long long> p;
        for(int i=0;i<n;i++){
             map<long long,long long> cr;
             if(nums[i]==k){
                c++;
             }


             cr[nums[i]]=1;
             for(auto x : p){
                if((x.first & nums[i]) == k){
                    c+=x.second;
                }
                cr[x.first & nums[i]] += x.second;  
             }
             p=cr;


        }
        return c;

    }
};
