class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long cnt=0;
        unordered_map<long long,long long>pfinal;
        for(int i=0;i<n;i++)
        {
            unordered_map<long long,long long>cfinal;
            if(nums[i]==k){
                ++cnt;
            }
            cfinal[nums[i]]=1;
            for(auto& [no,frq]:pfinal)
            {
                long long nfinal=no &nums[i];
                if(nfinal==k){
                    cnt+=frq;
                }
                cfinal[nfinal]+=frq;
            }
            pfinal=cfinal;
        }
        return cnt;

    }
};
