class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int, long long> mp; 
        long long ans = 0;
        int n = nums.size();
        for (int i = 0;i<n;i++) {
            unordered_map<int, long long> mp2;

            if (nums[i] == k) {
                ans++;
            }
            mp2[nums[i]]++;
            for (auto item : mp) {
                int andVal = item.first & nums[i];
                if (andVal == k) {
                    ans += item.second;
                }
                mp2[andVal] += item.second;
            }
            mp = mp2;
        }
        return ans;
    }
};
