class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        map<ll,ll>dp;
        ll ans=0;
        loo(i,nums.size()){
            if(nums[i]==k) ans++;
            map<ll,ll>ndp;
            for(auto it:dp){
                if((it.F&nums[i])==k){
                    ans+=it.S;

                }
                 if((it.F&nums[i])>=k){
                        ndp[(it.F&nums[i])]+=it.S;
                   }
            }
            ndp[nums[i]]++;
            dp=ndp;


        }
        return ans;

    }
};
