class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
      long long n = nums.size(), count = 0;

      map<long long, int> prevResults;  

      for (int i = 0; i < n; ++i) {
        map<long long, int> currResults; 

        if (nums[i] == k) ++count;

        currResults[nums[i]] = 1;

        for (auto it = prevResults.begin(); it != prevResults.end(); ++it) {
          long long newAndResult = it->first & nums[i];
          if (newAndResult == k) count += it->second;
          currResults[newAndResult] += it->second;
        }

        prevResults = currResults;
      }

      return count;
    }
};
