class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        arr, ans = [], 0
        for i, n in enumerate(nums):
            for j in range(len(arr)):
                arr[j][0] &= n
            arr.append([n, 1])
            for j in range(len(arr)-2, -1, -1):
                if arr[j][0] == arr[j+1][0]:
                    arr[j][1] += arr.pop(j+1)[1]
            for j in range(len(arr)):
                if arr[j][0] == k:
                    ans += arr[j][1]
#            print(arr)
        return ans
