#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        ll c=0;
        unordered_map<ll,ll> prev;
        for(auto i:nums){
            unordered_map<ll,ll> cur;
            if(i==k) c++;
            cur[i]=1;
            for(auto j:prev){
                ll nar=j.first&i;
                if(nar==k) c+=j.second;
                cur[nar]+=j.second;
            }
            prev=cur;
        }
        return c;
    }
};
