class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int size = array.size();
        long long ans = 0;
        unordered_map<long long, long long> previous;
        for (int i = 0; i < size; ++i) {
            unordered_map<long long, long long> current; 
            if (array[i] == target) {
                ++ans;
            }
            current[array[i]] = 1;
            for (auto& [value, frequency] : previous) {
                long long newAndResult = value & array[i];
                if (newAndResult == target) {
                    ans += frequency;
                }
                current[newAndResult] += frequency;
            }

            previous = current;
        }
        return ans;
    }
};
