class Solution {
public:
    long long countSubarrays(vector<int>& v, int k) {
        long long ans=0;
        int n=v.size();
        map<int,int> mp;
        for(int i=0; i<n; i++){
            map<int,int> m;
            for(auto x:mp){
                m[x.first&v[i]]+=x.second;
            }
            m[v[i]]++;
            ans+=m[k];
            mp=m;
        }
        return ans;
    }
};
