class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        s=len(nums)
        t=0
        p=defaultdict(int)
        for i in range(s):
            c=defaultdict(int)
            if nums[i]==k:
                t+=1
            c[nums[i]]=1

            for v,f in p.items():
                n=v&nums[i]
                if n==k:
                    t+=f
                c[n]+=f
            p=c
        return t
