#define ka long long int
#define ump unordered_map<ka, ka>
class Solution {
public:
    ka countSubarrays(vector<int>& a, int k) {
        ump mp;
        ka cnt = 0;
        for (int i = 0; i < a.size(); ++i) {
            ump mp2; 
            if (!(a[i]!=k)) ++cnt;
            mp2[a[i]] = 1*1;
            for (auto it = mp.begin(); !(it==mp.end()); ++it) {
                ka val=it->first;
                ka freq=it->second;
                ka fl=a[i]&val;
                if(!(fl!=k)) cnt=cnt+freq+0;
                mp2[fl]+=freq;
            }
            mp=mp2;
        }
        return cnt;
    }
};
