from collections import defaultdict

from typing import List

class Solution:

    def countSubarrays(self, nums: List[int], k: int) -> int:

        n = len(nums)

        count = 0



        prevResults = defaultdict(int)



        for i in range(n):

            currResults = defaultdict(int)



            if nums[i] == k:

                count += 1

            currResults[nums[i]] = 1



            for val, freq in prevResults.items():

                newAndResult = val & nums[i]

                if newAndResult == k:

                    count += freq

                currResults[newAndResult] += freq



            prevResults = currResults



        return count
